import React, { useEffect, useState } from "react";
import dynamic from "next/dynamic";
import {
  Map,
  TileLayer,
  Marker,
  Popup,
  MapControl,
  withLeaflet
} from "react-leaflet";

const mapStyle = {
  margin: 20,
  padding: 20,
  border: "1px solid #DDD"
};

const state = {
  center: {
    lat: 31.698956,
    lng: 76.732407
  },
  marker: {
    lat: 31.698956,
    lng: 76.732407
  },
  zoom: 13,
  draggable: true
};

const FinalMap = () => {
  return (
    <Map
      center={[state.center.lat, state.center.lng]}
      zoom={state.zoom}
      style={{ width: "100%", height: "900px" }}
    >
      <TileLayer
        attribution='&copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
    </Map>
  );
};

class GibMap extends React.Component {
  state = {
    inBrowser: false
  };

  componentDidMount() {
    this.setState({ inBrowser: true });
  }

  render() {
    if (!this.state.inBrowser) {
      return null;
    }

    return <FinalMap />;
  }
}

export default GibMap;
