import React, { useEffect, useState } from "react";
import dynamic from "next/dynamic";
import {
  Map,
  TileLayer,
  Marker,
  Popup,
  MapControl,
  withLeaflet
} from "react-leaflet";

const mapStyle = {
  margin: 20,
  padding: 20,
  border: "1px solid #DDD"
};

const state = {
  center: {
    lat: 31.698956,
    lng: 76.732407
  },
  marker: {
    lat: 31.698956,
    lng: 76.732407
  },
  zoom: 13,
  draggable: true
};

function RenderCompleted() {
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);

    return () => {
      setMounted(false);
    };
  });

  return mounted;
}

const GibMap = props => {
  const isMounted = RenderCompleted();

  return { isMounted };
};

export default GibMap;
