import react from "react";
import dynamic from "next/dynamic";
import {
  Map,
  TileLayer,
  Marker,
  Popup,
  MapControl,
  withLeaflet
} from "react-leaflet";

const mapStyle = {
  margin: 20,
  padding: 20,
  border: "1px solid #DDD"
};

const state = {
  center: {
    lat: 31.698956,
    lng: 76.732407
  },
  marker: {
    lat: 31.698956,
    lng: 76.732407
  },
  zoom: 13,
  draggable: true
};

class GibMap extends React.Component {
  state = {
    inBrowser: false
  };

  componentDidMount() {
    this.setState({ inBrowser: true });
  }

  render() {
    if (!this.state.inBrowser) {
      return null;
    }

    return (
      <Map
        center={position}
        zoom={state.zoom}
        style={{
          height: "700px"
        }}
      ></Map>
    );
  }
}

export default GibMap;
